package com.xworkz.methods;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 20;
		int b = 30;
		int c;
		int d;
		double h;
		float e;
		c = a + b;
		d = a * b;
		e = a / b;
		h = a % b;

		if (a < b) {

			System.out.println("addition of two no = " + c);
			System.out.println("multication of two no= " + d);
			System.out.println("division of two no= " + e);
			System.out.println("modulus of two no= " + h);
		}

	}

}
